#include "UserQueryObject.h"
#include "UserObject.h"

#include "graphqlservice/internal/Schema.h"

#include "graphqlservice/introspection/IntrospectionSchema.h"

#include <algorithm>
#include <functional>
#include <sstream>
#include <stdexcept>
#include <unordered_map>

using namespace std::literals;

namespace graphql::user {
namespace object {

UserQuery::UserQuery(std::unique_ptr<const Concept> pimpl) noexcept
	: service::Object{ getTypeNames(), getResolvers() }
	, _pimpl { std::move(pimpl) }
{
}

service::TypeNames UserQuery::getTypeNames() const noexcept
{
	return {
		R"gql(UserQuery)gql"sv
	};
}

service::ResolverMap UserQuery::getResolvers() const noexcept
{
	return {
		{ R"gql(GetUser)gql"sv, [this](service::ResolverParams&& params) { return resolveGetUser(std::move(params)); } },
		{ R"gql(GetUsers)gql"sv, [this](service::ResolverParams&& params) { return resolveGetUsers(std::move(params)); } },
		{ R"gql(__typename)gql"sv, [this](service::ResolverParams&& params) { return resolve_typename(std::move(params)); } }
	};
}

void UserQuery::beginSelectionSet(const service::SelectionSetParams& params) const
{
	_pimpl->beginSelectionSet(params);
}

void UserQuery::endSelectionSet(const service::SelectionSetParams& params) const
{
	_pimpl->endSelectionSet(params);
}

service::AwaitableResolver UserQuery::resolveGetUser(service::ResolverParams&& params) const
{
	auto argEntry = service::ModifiedArgument<int>::require("Entry", params.arguments);
	std::unique_lock resolverLock(_resolverMutex);
	service::SelectionSetParams selectionSetParams { static_cast<const service::SelectionSetParams&>(params) };
	auto directives = std::move(params.fieldDirectives);
	auto result = _pimpl->getGetUser(service::FieldParams { std::move(selectionSetParams), std::move(directives) }, std::move(argEntry));
	resolverLock.unlock();

	return service::ModifiedResult<User>::convert<service::TypeModifier::Nullable>(std::move(result), std::move(params));
}

service::AwaitableResolver UserQuery::resolveGetUsers(service::ResolverParams&& params) const
{
	std::unique_lock resolverLock(_resolverMutex);
	service::SelectionSetParams selectionSetParams { static_cast<const service::SelectionSetParams&>(params) };
	auto directives = std::move(params.fieldDirectives);
	auto result = _pimpl->getGetUsers(service::FieldParams { std::move(selectionSetParams), std::move(directives) });
	resolverLock.unlock();

	return service::ModifiedResult<User>::convert<service::TypeModifier::List>(std::move(result), std::move(params));
}

service::AwaitableResolver UserQuery::resolve_typename(service::ResolverParams&& params) const
{
	return service::Result<std::string>::convert(std::string{ R"gql(UserQuery)gql" }, std::move(params));
}

} // namespace object

void AddUserQueryDetails(const std::shared_ptr<schema::ObjectType>& typeUserQuery, const std::shared_ptr<schema::Schema>& schema)
{
	typeUserQuery->AddFields({
		schema::Field::Make(R"gql(GetUser)gql"sv, R"md()md"sv, std::nullopt, schema->LookupType(R"gql(User)gql"sv), {
			schema::InputValue::Make(R"gql(Entry)gql"sv, R"md()md"sv, schema->WrapType(introspection::TypeKind::NON_NULL, schema->LookupType(R"gql(Int)gql"sv)), R"gql()gql"sv)
		}),
		schema::Field::Make(R"gql(GetUsers)gql"sv, R"md()md"sv, std::nullopt, schema->WrapType(introspection::TypeKind::NON_NULL, schema->WrapType(introspection::TypeKind::LIST, schema->WrapType(introspection::TypeKind::NON_NULL, schema->LookupType(R"gql(User)gql"sv)))))
	});
}

} // namespace graphql::user
